/**
 * 数独求解器接口
 * 
 * OOAD设计原则：
 * 1. 依赖倒置：高层模块依赖此接口，而非具体实现
 * 2. 开闭原则：可以添加新的求解算法而不修改现有代码
 * 3. 接口隔离：定义清晰的求解器契约
 */
export class ISudokuSolver {
	/**
	 * 求解数独
	 * @param {number[][]} grid - 数独网格（可能不完整）
	 * @returns {number[][]} 完整解
	 * @throws {Error} 如果无解或输入无效
	 */
	solve(grid) {
		throw new Error('ISudokuSolver.solve() must be implemented');
	}

	/**
	 * 获取指定位置的解
	 * @param {number[][]} grid - 数独网格
	 * @param {number} x - 列索引
	 * @param {number} y - 行索引
	 * @returns {number|null} 该位置的解，如果无法确定则返回null
	 */
	getSolutionAt(grid, x, y) {
		const solution = this.solve(grid);
		return solution[y][x] || null;
	}
}